import {Component} from 'react'; //we need it for using Class
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    // this.state = { //initialize local state
    //   monster1: {
    //     name: 'Linda'
    //   },
    //   monster2: {
    //     name: 'Frank'
    //   },
    //   monster3: {
    //     name: 'Jacky'
    //   },
    // this.state = {
    //   monsters: [
    //     {
    //       name: 'Linda',
    //       id: 1,
    //     },
    //     {
    //       name: 'Ann',
    //       id: 2,
    //     },
    //     {
    //       name: 'Colt',
    //       id: 3,
    //     }

    //   ]
      this.state = {
      monsters: []
    //initialize local state
         };
    }

  componentDidMount(){ 
    fetch('https://jsonplaceholder.typicode.com/users')//moment when component mounted into DOM
//it is a promis
        .then((response) => response.json())
        .then((users) => this.setState(()=> {
          return {monsters: users}
        },
        ()=> {
          console.log(this.state)
        }
        ));
    }

    render() {
      return ( 
        <div className = "App">
          <input className='search-box' type='search' placeholder='search monsetrs' onChange={(event) => {
            console.log(event.target.value);
            const val = event.target.value.toLocaleLowerCase();
            const monsterSearch = this.state.monsters.filter(el => {
              return el.name.toLocaleLowerCase().includes(val);
            });
            console.log(monsterSearch);

            this.setState(()=>{
              return {monsters : monsterSearch}
            })

            if (!val) {
              console.log('canceled');
              this.setState(()=>{
                return {monsters : monsters}
              })
            }

            console.log(event);

            }}
          />

          {this.state.monsters.map((monster)=>{
              return <h1 key={monster.id}>{monster.name}</h1>
          }

          )}

        </div>
      );
    }
}

export default App;

