import {Component} from 'react'; //we need it for using Class
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = { //initialize local state
      monster1: {
        name: 'Linda'
      },
      monster2: {
        name: 'Frank'
      },
      monster3: {
        name: 'Jacky'
      },

    }
  }

    render() {
        return ( 
        <div className = "App">
          <h1>{this.state.monster1.name}</h1>
        </div>
      );
  }
}

export default App;

